import React from 'react';
import './specificCountry.css';
import {useState, useEffect} from 'react';
import {useParams} from 'react-router-dom';
import Button from '@mui/material/Button';
import { useNavigate, Link, Redirect } from "react-router-dom";
import {Col, Row} from 'react-bootstrap';

const SpecificCountry = ({countryName}) => {
    const obj = useParams();
    const [currentCountryName, setCurrentCountryName] = useState(obj.specificCountryName);
    const [currCountryBorders, setCurrCountryBorders] = useState([]);
    const [error, setError] = useState("");
    const [currCountryObj, setCurrCountryObject] = useState({});
    const navigate = useNavigate();


    /**
     * This retrieves the country's data
     * @param {*} countryName 
     */
    const retrieveSpecificCountryData = async (countryName) => {
        const response = await fetch(`https://restcountries.com/v2/name/${countryName}/?fullText=true`, {
                method: 'GET',
                headers: {
                    Accept: 'application/json',
                },
             });
        if (!response.ok) {
            setError("Unable to load the data. Please reload the page and try again");
            return;
        } 
            const specificCountryData = await response.json();
            setCurrCountryObject(specificCountryData[0]);
            setError('');
        // }

        let joinedCodes = "";
        if (currCountryObj.borders !== undefined) {
            let N = specificCountryData[0].borders.length;
            for (let i= 0; i < N; i++) {
                if (i != N - 1) {
                    joinedCodes.concat(specificCountryData[0].borders[i]);
                    joinedCodes.concat("");
                } else {
                    joinedCodes.concat(specificCountryData[0].borders[i]);
                }

            }
        }
        console.log(joinedCodes.toString());

        // const response1 = await fetch(`https://restcountries.com/v3.1/alpha?codes=${joinedCodes}`)
    }

    const navigateToNewPage = () => {
        navigate("/specificCountry/france", { replace: true });
        navigate(0);

    }

    const navigateBack = () => {
        navigate(-1);
    }

    useEffect(() => {
        retrieveSpecificCountryData(currentCountryName);
    }, []);



    if (error !== '') {

    } else {
        return (
            <>
                <div className="back_button">
                    <Button onClick={() => navigateBack()} variant="contained">Back</Button>
                </div>

                <div className="flags_and_stats">
                    <div className="flag_left">
                        <img src={currCountryObj.flag} className="country_flag"/>
                    </div>
                    <div className="information_right">
                        <div>
                            <Row>
                                <h5 className="country_name">{currCountryObj.name}</h5>
                            </Row>
                        </div>


                        <div className='stats_div'>
                            <Row className="topRow">
                                <Col><span style={{color:'white'}}> Native Name: {currCountryObj.nativeName}</span></Col>
                                {currCountryObj.topLevelDomain === undefined ? <span></span> : <Col><span>Top Level Domain: {currCountryObj.topLevelDomain[0]}</span></Col>}
                            </Row>
                            <Row>
                                {currCountryObj.population === undefined ? <span></span> :<Col><span>Population: {currCountryObj.population.toLocaleString()}</span></Col>}
                                {currCountryObj.currencies === undefined ? <span></span> :<Col><span>Currencies: {currCountryObj.currencies.map((each) => each.name)}</span></Col>}
                            </Row>
                            <Row>
                                <Col><span>Region: {currCountryObj.region}</span></Col>
                                {currCountryObj.languages === undefined ? <span></span> :<Col><span>Languages: {currCountryObj.languages.map((each) => each.name)}</span></Col>}
                            </Row>
                            <Row>
                                <Col><span>Sub Region: {currCountryObj.subregion}</span></Col>
                            </Row>
                            <Row>
                                <Col><span>Capital: {currCountryObj.capital}</span></Col>
                            </Row>
                        </div>

                        <div>

                            {currCountryObj.borders === undefined ?  <span>Borders: No borders </span> : 
                            currCountryObj.borders.map((each) => (
                                <Button onClick={(e) => navigateToNewPage(e.target.value)}>
                                    {each}
                                </Button>
                            ))
                            }
                        </div>    
                    </div>
                </div>
            </>
        )
    }


}

export default SpecificCountry;
