import React from 'react';
import './countries.css';
import {useState, useEffect, useRef} from 'react';
import Card from 'react-bootstrap/Card';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';
// import Container from 'react-bootstrap/Container';
import { Link } from "react-router-dom";
import region_options from '../../util/regions';
import Select from 'react-select';

/**
 * Represents all the countries - makes an API call to the countries API
 * upon loading the page
 * 
 * @returns the Countries object
 */
const Countries = () => {

    /*
        States:
            allCountries: the local representation of the countries
            isLoaded: a boolean flag that gets set to false once the
                      countries have loaded
            setError: if there is an error, this will show to the screen
    */
    const [allCountries, setAllCountries] = useState([]);
    const [isLoaded, setIsLoaded] = useState(false);
    const [error, setError] = useState("");
    const [currSearchText, setCurrSearchText] = useState('');
    const [currRegion, setCurrRegion] = useState('');
    const selectRegionRef = useRef();


    /**
     * Load the initial state of the data from the API
     */
    const retrieveAllData = async () => {
        const response = await fetch(`https://restcountries.com/v2/all`, {
                method: 'GET',
                headers: {
                    Accept: 'application/json',
                },
             });
        if (!response.ok) {
            setError("There was an error retrieving the data. Please reload the page and try again");
        }
        const countries = await response.json();
        // set the initial state in local storage
        localStorage.setItem("rest_countries_initial_state", JSON.stringify(countries));

        // set our state variables
        setAllCountries(countries);
        setIsLoaded(true);
    }


    /*
        gets called upon loading the page
    */
    useEffect(() => {
        retrieveAllData();
    },[]);

    /**
     * The call back function that gets executed whenever the user
     * filters countries by region.
     * @param {} selectedRegion 
     * @returns N/A
     */
    const getCountriesByRegion = async (selectedRegion) => {
        // If we have selected an actual region, then we make the async call
        if (selectedRegion !== 'all') {
            const response = await fetch(`https://restcountries.com/v2/region/${selectedRegion}`,{
            method: 'GET',
            headers: {
                Accept: 'application/json',
            },});

            // if we don't get a 200 response, we need to let the user know in the front end
            if (!response.ok) {
                setError("Unable to load the data for your specified region - Please try again");
                return;
            }

            const result = await response.json();
            setAllCountries(result);
            setError("");
            setIsLoaded(true);
        } 
        // If the user selects all, then we just retrieve all the data again.
        else {
            retrieveAllData();
            this.selectRegionRef.select.clearValue();
            setCurrRegion('');
        }

        // Now we need to make sure that we filter based on the text
        // we have in the search bar
        if (currSearchText !== '') {
            const filteredCountries = [];
            for (let i = 0; i < allCountries.length; i++) {
                let currCountry = allCountries[i];
                if (currCountry.name.toLowerCase().includes(currSearchText)) {
                    filteredCountries.push(currCountry);
                }
            }

            setAllCountries(filteredCountries);
        }
    }

    /**
     * The call back function that gets executed whenever
     * a new character is being typed in the search bar 
     * @param searchVal the country we are searching for
     */
    const getCountriesBySearch = (searchVal) => {

        // only if we have typed something do we execute the code
        if (searchVal !== '') {
            var lowerCaseSearchVal = searchVal.toLowerCase();
            let initialState = localStorage.getItem("rest_countries_initial_state");
            let actualInitialState = JSON.parse(initialState);
            const filteredCountries = [];
            for (let i = 0; i < actualInitialState.length; i++) {
                let currCountry = actualInitialState[i];
                if (currRegion.toLowerCase() !== 'all' && currRegion !== '') {
                    if (currCountry.region.toLowerCase() === currRegion.toLowerCase()) {
                        if (currCountry.name.toLowerCase().includes(lowerCaseSearchVal)) {
                            filteredCountries.push(currCountry);
                        }
                    }
                } else if (currRegion.toLowerCase() === 'all' || currRegion === '') {
                    if (currCountry.name.toLowerCase().includes(lowerCaseSearchVal)) {
                        filteredCountries.push(currCountry);
                    }
                }
            }

            setAllCountries(filteredCountries);

            // If we haven't found any countries, then we need to 
            // let the user know.
            if (allCountries.length === 0) {
                setError("Invalid search. Please try again.");
                setIsLoaded(true);
            } 
            // If we have found countries, then we set the error to be empty.
            else {
                setError("");
                setIsLoaded(true);
            }

        } 
        // if there is nothing in the search bar, then we simply retrieve the countries
        // from local storage
        else {
            let initialState = localStorage.getItem("rest_countries_initial_state");
            let actualInitialState = JSON.parse(initialState);
            setAllCountries(actualInitialState);
        }
    }

    /**
     * If we have an error we need to let the user know
     */
    if (error !== "") {
        return (
            <div className='search_bar_and_filter'>
                <div className='search_filter'>
                    <form onSubmit={(e) => e.preventDefault()}>
                        <input className="form-control" value={currSearchText} onChange={(e) => {
                            const val = document.getElementById("actual_search_bar").value;
                            setCurrSearchText(val);
                            getCountriesBySearch(val);
                        }} name="email" type="text" id="actual_search_bar"/>

                    </form>
                </div>

                <div className="region_filter">
                    <Select className="select_regions" options={region_options} value={region_options.value} onChange={(e) => {
                            let selectedRegion = e.value;
                            setCurrRegion(selectedRegion);
                            getCountriesByRegion(selectedRegion);
                        }} components={{
                            IndicatorSeparator: () => null
                            }} styles={{
                            control: (baseStyles, state) => ({
                                ...baseStyles,
                                border:'none',
                                backgroundColor: state.isSelected ? "hsl(209,23%,22%)" : "hsl(209,23%,22%)",
                                textDecoration:state.isSelected ? "white" : "black",

                            }),
                    }} placeholder="Filter by Region" ref={selectRegionRef}/>
                </div>

                {error}
            </div>
        )
    } 
    /**
     * skeleton loading - need to let the user know that the data is
     * on its way, so the user isn't left sitting with an empty screen
     * wondering what is going on.
     */
    else if (!isLoaded) {
        return <div className="container loading_msg">Loading...</div>
    } 
    /**
     * If everything works correctly, then we load the countries.
     */
    else {
        return (
            <>
                <div className="search_bar_and_filter">
                    <div className="search_filter">
                        <form onSubmit={(e) => e.preventDefault()}>
                            {/* <div class="input_icons"> */}
                                {/* <i class="fa fa-envelope icon"></i> */}
                                <input className="form-control" value={currSearchText} placeholder="Search for a country..." onChange={(e) => {
                                    const val = document.getElementById("actual_search_bar").value;
                                    setCurrSearchText(val);
                                    getCountriesBySearch(val);
                                }} name="search" type="text" id="actual_search_bar"/>
                            {/* </div> */}
                        </form>
                    </div>

                    <div className="region_filter">
                        <Select className="select_regions" options={region_options} value={region_options.value} onChange={(e) => {
                            let selectedRegion = e.value;
                            setCurrRegion(selectedRegion);
                            getCountriesByRegion(selectedRegion);
                        }} components={{
                            IndicatorSeparator: () => null
                            }} styles={{
                            control: (baseStyles, state) => ({
                                ...baseStyles,
                                border:'none',
                                backgroundColor: state.isSelected ? "hsl(209,23%,22%)" : "hsl(209,23%,22%)",
                                textDecoration:state.isSelected ? "white" : "black",                               
                            }),
                            }} placeholder="Filter by Region" ref={selectRegionRef}/>
                    </div>
                </div>

                <div className="countries_main_div">
                    <div className="actual_all_countries_block">
                        <Row className="g-4" xs={1} sm={2} md={2} lg={3} xl={4} xxl={4}> 
                            {allCountries.map((each, idx) => (
                                <Col>
                                    <div className='parent_card'>
                                        <Link to={`/specificCountry/${each.name}`} style={{textDecoration:'none'}}>
                                            <Card  className = "country_card border-0 mt-2 mr-4 d-flex h-100">
                                                <Card.Img variant="top" src={each.flags.png} className="card_image"/>

                                                <Card.Body className="card_body">
                                                    <Card.Title className="card_title"><div>{each.name}</div></Card.Title>
                                                    <Card.Text className="card_text">
                                                        <span className='card_stats'>Population: </span> <span className="actual_population">{each.population.toLocaleString()}</span> <br/>
                                                        <span className='card_stats'>Region: </span><span className="actual_region">{each.region} </span><br/>
                                                        <span className='card_stats'>Capital: </span><span className="actual_capital"> {each.capital}</span>
                                                    </Card.Text>
                                                </Card.Body>
                                            </Card>
                                        </Link>
                                    </div>

                                </Col>  

                            ))}
                        </Row>
                    </div>

                </div>
            </>
        )
    }
}

export default Countries;
