import React from 'react';
import './specificCountry.css';
import {useState, useEffect} from 'react';
import {useParams} from 'react-router-dom';

const SpecificCountry = () => {
    const obj = useParams();
    const [currentCountryName, setCurrentCountryName] = useState(obj.specificCountryName);

    const retrieveSpecificCountryData = async () => {
        const response = await fetch(`https://restcountries.com/v2/all`, {
                method: 'GET',
                headers: {
                    Accept: 'application/json',
                },
             });
        if (!response.ok) {
            setError("There was an error retrieving the data. Please reload the page and try again");
        }
        const countries = await response.json();
        // set the initial state in local storage
        localStorage.setItem("rest_countries_initial_state", JSON.stringify(countries));

        // set our state variables
        setAllCountries(countries);
        setIsLoaded(true);
    }


    return (
        <>
            <div className="hello">
                <span className="hello1">{currentCountryName}</span>
            </div>
        </>
    )
}

export default SpecificCountry;
