import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import './header.css';
import '@fortawesome/free-solid-svg-icons'
import { useNavigate, Link, Redirect } from "react-router-dom";
import {useState, useEffect} from 'react';
import e from 'cors';

const Header = () => {

    const [theme, setTheme] = useState("light");
    useEffect(() => {
        if (theme === 'dark') {
            document.documentElement.classList.add("dark");
        } else {
            document.documentElement.classList.remove("dark");
        }
    }, [theme])

    const handleThemeSwitch = () => {
        if (theme === "dark") {
            setTheme("light");
        } else {
            setTheme("dark");
        }
    }

    return (
        <>
        <div class="nav navbar-expanded-lg">
            <div class="flex flex-row shadow-xl justify-between w-full pt-4 pb-4 custom-background dark:bg-white fixed z-10">
                <div class="sm:inline-block global md:inline-block sm:w-full xs:w-full xxs:w-full md:w-[40%]">
                    <a class="text dark:where_in_world_color text-black" href="/">Where in the world?</a>
                </div>

                <div class="sm:inline-block md:inline-block right-0 mr-[5%] pt-2"><span className="dm-text" onClick={() => handleThemeSwitch()}><i class="fa-solid fa-moon"></i> <span className="text-black dark:text-white">Dark Mode</span></span></div>
            </div>
        </div>
        </>

    )
}

export default Header;
