import base_url from "../util/baseUrl";

const getAllCountries = () => fetch(base_url + `/all`, {
    method: 'GET',
    headers: {
        Accept: 'application/json',
    },
    });

const getCountryByName = (countryName) => {
    fetch(base_url + `name/${countryName}`)
}

export default getAllCountries;
