import React from 'react';
import './countries.css';
import {useState, useEffect} from 'react';
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';



const Countries = () => {

    const [allCountries, setAllCountries] = useState([]);
    const [isLoaded, setIsLoaded] = useState(false);
    const [error, setError] = useState(null);



    useEffect(() => {
        fetch(`https://restcountries.com/v2/all`).
            then(response=> response.json()).then((json) => {
                setIsLoaded(true);
                setAllCountries(json);
            }).catch(err => {
                setIsLoaded(true);
                setError(err);
                console.log(err);
            })
    },[]);






    if (error) {
        return <div>Error: {error.message}</div>
    } else if (!isLoaded) {
        return <div className="container countries_header">Loading...</div>
    } else {
        return (
            <>
                <div className="container countries_header">
                    <ul> 
                        {allCountries.map((each) =>

                                <Card style={{ width: '18rem' }}>
                                    <Card.Img variant="top" src={each.png} />
                                    <Card.Body>
                                        <Card.Title className="card_title">{each.name}</Card.Title>
                                        <Card.Text className="card_text">
                                            {each.name}
                                        </Card.Text>
                                    </Card.Body>
                                </Card>

                        )}
                    </ul>
                </div>
            </>
        )
    }
}

export default Countries;
