import base_url from "../util/baseUrl";

const getAllCountries = () => fetch(base_url + `/all`, {
    method: 'GET',
    headers: {
        Accept: 'application/json',
    },
});

const getCountryByName = (countryName) => fetch(base_url + `/name/${countryName}/?fullText=true`, {
    method: 'GET',
    headers: {
        Accept: 'application/json',
    },
})

const getBorderInformation = (countryCodes) => fetch(`https://restcountries.com/v2/alpha?codes=${joinedCodes}`);



const functions = {getAllCountries, getCountryByName, getBorderInformation}

export default functions;
