import React from 'react';
import './specificCountry.css';
import {useState, useEffect} from 'react';
import {useParams} from 'react-router-dom';
import Button from '@mui/material/Button';
import { useNavigate } from "react-router-dom";
import {Col, Row} from 'react-bootstrap';

const SpecificCountry = () => {
    const obj = useParams();
    const [currentCountryName, setCurrentCountryName] = useState(obj.specificCountryName);
    const [isLoaded, setIsLoaded] = useState(false);
    const [error, setError] = useState("");
    const [currCountryObj, setCurrCountryObject] = useState({});
    const navigate = useNavigate();


    const retrieveSpecificCountryData = async (countryName) => {
        const response = await fetch(`https://restcountries.com/v2/name/${countryName}/?fullText=true`, {
                method: 'GET',
                headers: {
                    Accept: 'application/json',
                },
             });
        if (!response.ok) {
            setError("Unable to load the data. Please reload the page and try again");
        }
        const specificCountryData = await response.json();
        setCurrCountryObject(specificCountryData[0]);
        setError('');

    }

    useEffect(() => {
        retrieveSpecificCountryData(currentCountryName);
    },[]);

    console.log(currCountryObj);

    if (error !== '') {

    } else {
        return (
            <>
                <div className="back_button">
                    <Button onClick={() => navigate(-1)} variant="contained">Back</Button>
                </div>

                <div className="flags_and_stats">
                    <div className="flag_left">
                        <img src={currCountryObj.flag} className="country_flag"/>
                        {/* {currCountryObj.flag} */}
                    </div>
                    <div className="information_right">
                        <div>
                            <Row>
                                <h5 className="country_name">{currCountryObj.name}</h5>
                            </Row>
                        </div>


                        <div>
                            <Row className="topRow">
                                <Col><span style={{color:'white'}}> Native Name: {currCountryObj.nativeName}</span></Col>
                                <Col>Top Level Domain: {currCountryObj.topLevelDomain[0]}</Col>
                            </Row>
                            <Row>
                                <Col><span>Population: {currCountryObj.population.toLocaleString()}</span></Col>
                                {/* <Col><span>Currencies: {currCountryObj.currencies.map((each) => each.name)}</span></Col> */}
                            </Row>
                            <Row>
                                <Col><span>Region: {currCountryObj.region}</span></Col>
                                <Col><span>Languages: {currCountryObj.languages.map((each) => each.name)}</span></Col>
                            </Row>
                            <Row>
                                <Col><span>Sub Region: {currCountryObj.subregion}</span></Col>
                            </Row>
                        </div>

                    </div>
                </div>
            </>
        )
    }


}

export default SpecificCountry;
