import React from 'react';
import './countries.css';
import {useState, useEffect} from 'react';
import Card from 'react-bootstrap/Card';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';
import Container from 'react-bootstrap/Container';
import { Link } from "react-router-dom";
import Dropdown from 'react-bootstrap/Dropdown';
import DropdownButton from 'react-bootstrap/DropdownButton';
import Select from 'react-select';



/**
 * Represents all the countries - makes an API call to the countries API
 * upon loading the page
 * 
 * @returns the Countries object
 */
const Countries = () => {

    /*
        States:
            allCountries: the local representation of the countries
            isLoaded: a boolean flag that gets set to false once the
                      countries have loaded
            setError: if there is an error, this will show to the screen
    */
    const [allCountries, setAllCountries] = useState([]);
    const [isLoaded, setIsLoaded] = useState(false);
    const [error, setError] = useState(null);
    const [region, setRegion] = useState('');

    /*
        gets called upon loading the page
    */
    useEffect(() => {
        const fetchCountryData = async () => {
            const response = await fetch(`https://restcountries.com/v2/all`, {
                    //mode:'no-cors'
                    method: 'GET',
                    headers: {
                        Accept: 'application/json',
                    },
                 });
                 const countries = await response.json();
                 setAllCountries(countries);
                 setIsLoaded(true);
        }
        fetchCountryData();
    },[]);


    const getCountriesByRegion = async (selectedRegion) => {
        const response = await fetch(`https://restcountries.com/v2/region/${selectedRegion}`,{
            method: 'GET',
            headers: {
                Accept: 'application/json',
            },
         });
        const result = await response.json();
        setAllCountries(result);

    }

    const options = [
        { value: 'africa', label: 'Africa' },
        { value: 'americas', label: 'America' },
        { value: 'asia', label: 'Asia' },
        { value: 'europe', label: 'Europe' },
        { value: 'oceania', label: 'Oceania' }
      ]


    /**
     * If we have an error we need to let the user know
     */
    if (error) {
        return <div>Error: {error.message}</div>
    } 
    /**
     * skeleton loading - need to let the user know that the data is
     * on its way, so the user isn't left sitting with an empty screen
     * wondering what is going on.
     */
    else if (!isLoaded) {
        return <div className="container countries_header">Loading...</div>
    } 
    /**
     * If everything works correctly, then we load the countries.
     */
    else {
        return (
            <>

                <div className="search_bar_and_filter">


                <Row >
                    <Col xs="6">
                        <div>
                            hi hello
                        </div>
                    </Col>

                    <Col xs="12"> 
                        <div className="region_filter">
                            <Select className="select_regions" options={options} value={options.value} onChange={(e) => {
                                let selectedRegion = e.value;
                                setRegion(selectedRegion);
                                getCountriesByRegion(selectedRegion);
                            }} styles={{
                                control: (baseStyles, state) => ({
                                  ...baseStyles,
                                  border:'none',
                                //   borderColor: state.isFocused ? 'grey' : 'red',
                                  backgroundColor:'hsl(209,23%,22%)',
                                  color:'white'
                                }),
                              }}/>
                        </div>
                        <span style={{color:"white"}}>{region}</span>
                    </Col>
                </Row>
                </div>


                <div className="countries_main_div">
                    <div className="actual_all_countries_block">
                        <Row className="g-4" xs={1} sm={2} md={2} lg={3} xl={4} xxl={4}> 
                            {allCountries.map((each, idx) => (
                                <Col>
                                    <div className='parent_card'>
                                        <Link to={`/specificCountry/${each.name}`} style={{textDecoration:'none'}}>
                                            <Card  className = "country_card border-0 mt-2 mr-4 d-flex h-100">
                                                <Card.Img variant="top" src={each.flags.png} className="card_image"/>

                                                <Card.Body className="card_body">
                                                    <Card.Title className="card_title"><div>{each.name}</div></Card.Title>
                                                    <Card.Text className="card_text">
                                                        <span className='card_stats'>Population: </span> <span className="actual_population">{each.population.toLocaleString()}</span> <br/>
                                                        <span className='card_stats'>Region: </span><span className="actual_region">{each.region} </span><br/>
                                                        <span className='card_stats'>Capital: </span><span className="actual_capital"> {each.capital}</span>
                                                    </Card.Text>
                                                </Card.Body>
                                            </Card>
                                        </Link>
                                    </div>

                                </Col>  

                            ))}
                        </Row>
                    </div>

                </div>
            </>
        )
    }
}

export default Countries;
