import React from 'react';
import './countries.css';
import {useState, useEffect} from 'react';
import Card from 'react-bootstrap/Card';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';


/**
 * Represents all the countries - makes an API call to the countries API
 * upon loading the page
 * 
 * @returns the Countries object
 */
const Countries = () => {

    /*
        States:
            allCountries: the local representation of the countries
            isLoaded: a boolean flag that gets set to false once the
                      countries have loaded
            setError: if there is an error, this will show to the screen
    */
    const [allCountries, setAllCountries] = useState([]);
    const [isLoaded, setIsLoaded] = useState(false);
    const [error, setError] = useState(null);

    /*
        gets called upon loading the page
    */
    useEffect(() => {
        fetch(`https://restcountries.com/v3.1/all`).
            then(response=> response.json()).then((json) => {
                setIsLoaded(true);
                setAllCountries(json);
            }).catch(err => {
                setIsLoaded(true);
                setError(err);
            })
    },[]);


    /**
     * If we have an error we need to let the user know
     */
    if (error) {
        return <div>Error: {error.message}</div>
    } 
    /**
     * skeleton loading - need to let the user know that the data is
     * on its way, so the user isn't left sitting with an empty screen
     * wondering what is going on.
     */
    else if (!isLoaded) {
        return <div className="container countries_header">Loading...</div>
    } 
    /**
     * If everything works correctly, then we load the countries.
     */
    else {
        return (
            <>
                <div className="container countries_header">
                    <Row className="g-4"> 
                        {allCountries.map((each, idx) => (
                            <Col>
                                <Card  className = "country_card" style={{ width: '18rem', cursor: 'pointer', hover: {
                                    transform:'scale(1.5)'
                                }}} onClick={() => console.log("CLICKED " + each.name)}>
                                    <Card.Img variant="top" src={each.flags.png} className="card_image"/>
                                    <Card.Body>
                                        <Card.Title className="card_title">{each.name}</Card.Title>
                                        <Card.Text className="card_text">
                                            <span className='card_stats'>Population: </span>{each.population.toLocaleString()} <br/>
                                            <span className='card_stats'>Region: </span>{each.region} <br/>
                                            <span className='card_stats'>Capital: </span>{each.capital}
                                        </Card.Text>
                                    </Card.Body>
                                </Card>
                            </Col>  
                        ))}
                    </Row>
                </div>
            </>
        )
    }
}

export default Countries;
