import React from 'react';
import './specificCountry.css';
import {useState, useEffect} from 'react';
import {useParams} from 'react-router-dom';
import Button from '@mui/material/Button';
import { useNavigate, Link, Redirect } from "react-router-dom";
import {Col, Row} from 'react-bootstrap';

const SpecificCountry = ({countryName}) => {
    const obj = useParams();
    const [currentCountryName, setCurrentCountryName] = useState(obj.specificCountryName);
    const [currCountryBorders, setCurrCountryBorders] = useState([]);
    const [isLoaded, setIsLoaded] = useState(false);
    const [error, setError] = useState("");
    const [currCountryObj, setCurrCountryObject] = useState({});
    const navigate = useNavigate();
    const [currLanguages, setCurrLanguages] = useState('');


    /**
     * This retrieves the country's data
     * @param {*} countryName 
     */
    const retrieveSpecificCountryData = async (countryName) => {
        const response = await fetch(`https://restcountries.com/v2/name/${countryName}/?fullText=true`, {
                method: 'GET',
                headers: {
                    Accept: 'application/json',
                },
             });
        if (!response.ok) {
            setError("Unable to load the data. Please reload the page and try again");
            return;
        } 
        const specificCountryData = await response.json();
        setCurrCountryObject(specificCountryData[0]);
        setError('');
        setIsLoaded(true);
        console.log(specificCountryData[0]);

        if (specificCountryData[0].borders !== undefined) {
            let joinedCodes = specificCountryData[0].borders.join();
            const response1 = await fetch(`https://restcountries.com/v2/alpha?codes=${joinedCodes}`);
            if (!response1.ok) {
                setError("Unable to load the data. Please reload the page and try again");
                return;
            } else {
                const jsonData = await response1.json();
                let names = [];
                for (let i = 0; i < jsonData.length; i++) {
                    names.push(jsonData[i].name);
                }
                setIsLoaded(true);
                setCurrCountryBorders(names);

                let languages = '';
                for (let i = 0; i < specificCountryData[0].languages.length; i++) {
                    languages = languages + specificCountryData[0].languages[i].name + ', '
                }

                languages = languages.substring(0, languages.length - 2);
                setCurrLanguages(languages);

            }



        }

    }

    const navigateToNewPage = (countryName) => {
        navigate(`/specificCountry/${countryName}`, { replace: true });
        navigate(0);

    }

    const navigateBack = () => {
        navigate(-1);
    }

    useEffect(() => {
        retrieveSpecificCountryData(currentCountryName);
    }, []);

    if (error !== '') {

    } 
    else if (!isLoaded) {
        return <div className="container loading_msg">Loading...</div>
    } 

    else {
        return (
            <div className="parent">

                <div className="back_button">
                    <Button onClick={() => navigateBack()} variant="contained">Back</Button>
                </div>

                <div className="flags_and_stats">
                    <div className="flag_left">
                        <img src={currCountryObj.flag} className="country_flag object-contain"/>
                    </div>
                    <div className="information_right">
                        <div>
                                <h5 className="country_name">{currCountryObj.name}</h5>
                        </div>
                        <div className='stats_div' style={{color:'white'}}>
                            <div className='top_stats'>
                                <div> Native Name: {currCountryObj.nativeName}</div>
                                {currCountryObj.population === undefined ? <div></div> : <div>Population: {currCountryObj.population.toLocaleString()}</div>}
                                <div>Region: {currCountryObj.region}</div>
                                <div>Sub Region: {currCountryObj.subregion}</div>
                                <div>Capital: {currCountryObj.capital}</div>
                            </div>
                            <div className='bottom_stats'>
                                {currCountryObj.topLevelDomain === undefined ? <div></div> : <div>Top Level Domain: {currCountryObj.topLevelDomain[0]}</div>}
                                {currCountryObj.currencies === undefined ? <div></div> : <div> Currencies: {currCountryObj.currencies.map((each) => each.name)}</div>}    
                                {currCountryObj.languages === undefined ? <div></div> : <div>Languages: {currLanguages}</div>}    
                            </div>
                        </div>

                        <div style={{color:'white'}}>

                            {currCountryBorders.length === 0 ?  <span style={{color:'white'}}> </span> : <span style={{color:'white'}}> Borders: 
                            {currCountryBorders.map((each) => (
                                <Button value={each} onClick={(e) => navigateToNewPage(e.target.value)}>
                                     {each}
                                </Button>
                            ))}
                            </span> }


                        </div>    
                    </div>
                </div>
            </div>
        )
    }


}

export default SpecificCountry;
